// â”€â”€â”€ GitHub API helpers â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€

function req(method, path, body) {
  return new Promise((resolve, reject) => {
    const data = body ? JSON.stringify(body) : undefined;
    const options = {
      hostname: "api.github.com",
      path,
      method,
      headers: {
        "Authorization": `Bearer ${TOKEN}`,
        "Accept":        "application/vnd.github+json",
        "User-Agent":    "zm-push-script",
        "X-GitHub-Api-Version": "2022-11-28",
        ...(data ? { "Content-Type": "application/json", "Content-Length": Buffer.byteLength(data) } : {}),
      },
    };
    const r = https.request(options, (res) => {
      let raw = "";
      res.on("data", c => raw += c);
      res.on("end", () => {
        try { resolve({ status: res.statusCode, body: JSON.parse(raw) }); }
        catch { resolve({ status: res.statusCode, body: raw }); }
      });
    });
    r.on("error", reject);
    if (data) r.write(data);
    r.end();
  });
}

async function getSHA(fp) {
  const r = await req("GET", `/repos/${OWNER}/${REPO}/contents/${fp}`);
  if (r.status === 200) return r.body.sha;
  return null;
}

function delay(ms) { return new Promise(r => setTimeout(r, ms)); }

async function push(fp, b64content, msg) {
  for (let a = 1; a <= 3; a++) {
    const sha  = await getSHA(fp);
    const body = { message: msg, content: b64content };
    if (sha) body.sha = sha;
    const r = await req("PUT", `/repos/${OWNER}/${REPO}/contents/${fp}`, body);
    if (r.status === 200 || r.status === 201) { console.log(`âœ… ${fp}`); return true; }
    if (r.status === 409) { await delay(2500 * a); continue; }
    console.error(`âŒ ${fp} â€” HTTP ${r.status}`, r.body?.message || "");
    return false;
  }
  console.error(`âŒ ${fp} â€” max retries`);
  return false;
}

